package academy.timetable.action;import java.io.PrintWriter;import java.util.List;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;import academy.timetable.db.TimetableDAO;public class TeachersTimeAction implements Action {	@Override	public ActionForward execute(HttpServletRequest request,			HttpServletResponse response) throws Exception {		ActionForward forward = new ActionForward();		/* request settting */		request.setCharacterEncoding("utf-8");		/* session 검증 */		HttpSession session = request.getSession();		int level = Integer.parseInt((String)session.getAttribute("level"));		if (level < 3) {			response.setContentType("text/html;charset=UTF-8");			PrintWriter out = response.getWriter();			out.println("<script>");			out.println("alert('해당 항목에 접근할 권한이 없습니다.')");			out.println("history.back()");			out.println("</script>");			out.close();			return null;		}				/* session 검증 */		String id=(String)session.getAttribute("id");		List myTime=new TimetableDAO().getmyTimetable(id);				request.setAttribute("timelist", myTime);		forward.setPath("./timetable/ViewTimeTable.jsp");		forward.setRedirect(false);		return forward;	}}