package academy.timetable.action;import java.util.List;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import javax.servlet.http.HttpSession;import academy.groups.db.GroupsDAO;import academy.timetable.db.TimetableDAO;public class TimeTableListAction implements Action {	@Override	public ActionForward execute(HttpServletRequest request,			HttpServletResponse response) throws Exception {		ActionForward forward = new ActionForward();		request.setCharacterEncoding("utf-8");		HttpSession session = request.getSession();		int level =  Integer.parseInt((String)session.getAttribute("level"));		if (level <= 3) {			response.sendRedirect("./");		}		String gp_idx = null;		if (request.getParameter("gp_idx") != null) {			gp_idx = request.getParameter("gp_idx");		}		String groupsName=request.getParameter("groupsName");		System.out.println("TimeTableListAction-->");		TimetableDAO timetable = new TimetableDAO();		List glist = timetable.getGpList();		List list = timetable.getTimetable(gp_idx);		request.setAttribute("gp_idx", gp_idx);		request.setAttribute("grouplist", glist);		request.setAttribute("timetable", list);		System.out.println(groupsName);		request.setAttribute("groupsName",groupsName);		forward.setPath("./timetable/timeTablelist.jsp");		forward.setRedirect(false);		return forward;	}}